sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    console.log('kris', {
        api,
        event
    });
    const {
        query,
        title,
        reqType
    } = api.state.listOptions;

    const newQuery = event.payload.query || query;
    const newReqType = event.payload.reqType || reqType;
    let newTitle = (event.payload.title || title).split(' - ')[0];
    const newEncodedQuery = [newQuery];

    if (newReqType && newReqType !== 'all') {
        newTitle = `${newTitle} - ${newReqType}`;
        newEncodedQuery.push(`u_request_type=${newReqType}`);
    }

    api.setState('listOptions', {
        query: newQuery,
        title: newTitle,
        reqType: newReqType,
        encodedQuery: newEncodedQuery.join('^'),
    });
}
